import React,{useState ,useEffect} from 'react'


export default function Search({selectTab}) {


    const[value, Setvalue]=useState(" ");


     const search = `https://api.themoviedb.org/3/search/movie?api_key=77c57d7fc2e5ee5e5664c1a03edf9f29&language=en-US&query=${value}&page=1&include_adult=false`;


    const handleSubmit = (e) => {
        e.preventDefault();
       const get = () =>{
	fetch(search)
	.then((res) => res.json())
	.then((data) => {	
              selectTab(data.results)
	})
     }

   useEffect(() => {
	get();
   },[])

}
    const handleSearch =(e) =>{
        Setvalue(e.target.value)

        console.log(e.target.value)
    }
    return (
        <form onSubmit={handleSubmit}>
            <input
             value={value}
             onChange={handleSearch}
             type="text"
             placeholder="search for a movie"i
             className="searchbox"
            />
           <button type="submit">Search</button>

        </form>
    )
}
