import React,{useState} from 'react'
import SearchPage from './SearchPage';



export default function Search({ selectTab, search ,setSearch }) {



   const find = `https://api.themoviedb.org/3/search/movie?api_key=77c57d7fc2e5ee5e5664c1a03edf9f29&language=en-US&query=${search}&page=1&include_adult=false`;


    const handleSubmit = (e) => {
        e.preventDefault();

	fetch(find)
	.then((res) => res.json())
	.then((data) => selectTab(data?.results))
}

     const handleSearch =(e) =>{
	  setSearch(e.target.value)

	selectTab?.('search')

    }
    return (
      <>
        <form onSubmit={handleSubmit}>
            <input
             value={search}
             onChange={handleSearch}
             type="text"
             placeholder="search for a movie"i
             className="searchbox"
            />
	     <button type="submit">Search</button>
          </form>
            <div className="row">
              {data?.map((movie) => {
                return (
                  <div className="column" key={movie?.id}  >
                    <img src={`https://image.tmdb.org/t/p/w500${movie?.poster_path}`} alt=""   />
                    <h5>{movie?.title}</h5>
                    <p>{movie?.overview}</p>
                    <p>Rating: {movie?.vote_average} ({movie?.vote_count} votes)</p>
                   </div>
                )
              })}
              </div> 
       </>
    )
}
