import React,{useState} from 'react'



export default function Search({selectTab, selectedTab}) {



console.log(selectTab)
   const[value, Setvalue]=useState(" ");

   const search = `https://api.themoviedb.org/3/search/movie?api_key=77c57d7fc2e5ee5e5664c1a03edf9f29&language=en-US&query=${value}&page=1&include_adult=false`;

   console.log(selectedTab)

    const handleSubmit = (e) => {
        e.preventDefault();

	fetch(search)
	.then((res) => res.json())
	.then((data) => selectTab(data?.results))
}

     const handleSearch =(e) =>{
        Setvalue(e.target.value)

        console.log(e.target.value)
    }
    return (
     <>
        <form onSubmit={handleSubmit}>
            <input
             value={value}
             onChange={handleSearch}
             type="text"
             placeholder="search for a movie"i
             className="searchbox"
            />
	     <button type="submit">Search</button>
          </form>
          <div>


            <div className="row">
              {selectedTab?.map((movie) => { <Search/>
                return (
                  <div className="column" key={movie?.id}  >
                    <img src={`https://image.tmdb.org/t/p/w500${movie?.poster_path}`} alt=""   />
                    <h5>{movie?.title}</h5>
                    <p>{movie?.overview}</p>
                    <p>Rating: {movie?.vote_average} ({movie?.vote_count} votes)</p>


                  </div>
                )
              })}
              </div> 

	  </div>



    </>
    )
}
