import React,{useState} from 'react'
import useFetch from '../hooks/useFetch'


const Popular = (props) => {
  const SingleMovie = props.SingleMovie

  const { loading, response, error } = useFetch('/popular')

  const [click ,setClick] = useState([])

  const handleClick = (Movie) => {
		const findMovie = [...click, movie];
		setClick(findMovie);

   console.log(findMovie)
	}

  console.log('popular', { loading, response, error })
  return (
    <div>
      <h3>Popular Movies</h3>
      {loading
        ? <span>Loading...</span>
        : error
          ? <span>{error?.message}</span>
          : (
            <div className="row">
              {response?.results?.map((movie) => {
                return (
                  <div className="column" key={movie?.id} >
                    <img src={`https://image.tmdb.org/t/p/w500${movie?.poster_path}`} alt=""   />
                    <h5>{movie?.title}</h5>
                    <p>{movie?.overview}</p>
                    <p>Rating: {movie?.vote_average} ({movie?.vote_count} votes)</p>
                    <div>
                      <SingleMovie/>
                      onClick={handleClick(movie)}

                    </div>
                  </div>
                )
              })}
            </div>
          )}
    </div>
  )
}

export default Popular
