import React,{useState} from 'react'
import useFetch from '../hooks/useFetch'

const Popular = ({click, setClick, selectTab}) => {
  const { loading, response, error } = useFetch('/popular')



  const clickMe = ({id}) => {
		const findTodo = response?.result?.find ((movie) => movie.id !== id);
		setClick(findTodo);
	}

  console.log('popular', { loading, response, error })
  return (
    <div>
      <h3>Popular Movies</h3>
      {loading
        ? <span>Loading...</span>
        : error
          ? <span>{error?.message}</span>
          : (
            <div className="row">
              {response?.results?.map((movie) => {
                return (
                  <div className="column" key={movie?.id} onClick={clickMe}>
                    <img src={`https://image.tmdb.org/t/p/w500${movie?.poster_path}`} alt=""  />
                    <h5>{movie?.title}</h5>
                    <p>{movie?.overview}</p>
                    <p>Rating: {movie?.vote_average} ({movie?.vote_count} votes)</p>
                  </div>
                )
              })}
            </div>
          )}
    </div>
  )
}

export default Popular
